public function DisplayBareRelations(WebPage $oP, $bEditMode = false)
   {
       // Execute parent function first
       parent::DisplayBareRelations($oP, $bEditMode);

       if ($this->GetDisplayMode() == cmdbAbstractObject::ENUM_DISPLAY_MODE_VIEW) {
           // Search for ConnectableCIs linked to NetworkDevices that make the cluster
           $sOQL = "SELECT ConnectableCI AS c JOIN lnkConnectableCIToNetworkDevice AS l ON l.connectableci_id = c.id JOIN NetworkDevice AS nd ON l.networkdevice_id = nd.id JOIN ClusterNetwork AS cn ON nd.clusternetwork_id = cn.id WHERE cn.id = :id AND l.connection_type='downlink'";
           $oConnectableCIsSet =  new CMDBObjectSet(DBObjectSearch::FromOQL($sOQL), array(), array('id' => $this->GetKey()));
           $sName = Dict::S('Class:ClusterNetwork/Tab:connectablecis_list');
           $sTitle = Dict::S('Class:ClusterNetwork/Tab:connectablecis_list+');
           TeemIp\TeemIp\Extension\Framework\Helper\IPUtils::DisplayTabContent($oP, $sName, 'connectable_cis', 'ConnectableCI', $sTitle, '', $oConnectableCIsSet, false);
      }
   }

public function AfterInsert()
   {
       // Execute parent function first
       parent::AfterInsert();

       TeemIp\TeemIp\Extension\IPManagement\Model\_IPAddress::SetStatusOnAttachment($this->Get('ipaddress_id'));
   }

public function AfterDelete()
   {
       // Execute parent function first
       parent::AfterDelete();

       TeemIp\TeemIp\Extension\IPManagement\Model\_IPAddress::SetStatusOnDetachment($this->Get('ipaddress_id'));
   }

	public function AfterInsert()
    {
        parent::AfterInsert();

        $iPeerId = $this->Get('peer_id');
        if ($iPeerId > 0)
        {
            $oPeer = MetaModel::GetObject('AggregateLink', $iPeerId, false);
            if (!is_null($oPeer))
            {
                $oPeer->Set('peer_id', $this->GetKey());
                $oPeer->DBUpdate();
            }
        }
	}

	public function AfterUpdate()
    {
        parent::AfterUpdate();

		$aListChanges = $this->ListPreviousValuesForUpdatedAttributes();
        if (array_key_exists('peer_id', $aListChanges))
        {
            // Reset peer on old remote
            $iPreviousPeerId = $aListChanges['peer_id'];
            if ($iPreviousPeerId > 0)
            {
                $oPreviousPeer = MetaModel::GetObject('AggregateLink', $iPreviousPeerId, false);
                if (!is_null($oPreviousPeer))
                {
                    $oPreviousPeer->Set('peer_id', 0);
                    $oPreviousPeer->DBUpdate();
                }
            }
            // Set peeron new remote
            $iPeerId = $this->Get('peer_id');
            if ($iPeerId > 0)
            {
                $oPeer = MetaModel::GetObject('AggregateLink', $iPeerId, false);
                if (!is_null($oPeer))
                {
                    $oPeer->Set('peer_id', $this->GetKey());
                    $oPeer->DBUpdate();
                }
            }
        }
	}

	public function AfterDelete()
    {
        parent::AfterDelete();

        $iPeerId = $this->Get('peer_id');
        if ($iPeerId > 0)
        {
            $oPeer = MetaModel::GetObject('AggregateLink', $iPeerId, false);
            if (!is_null($oPeer))
            {
                $oPeer->Set('peer_id', 0);
                $oPeer->DBUpdate();
            }
        }
	}

public function DisplayBareRelations(WebPage $oP, $bEditMode = false)
   {
       // Execute parent function first
       parent::DisplayBareRelations($oP, $bEditMode);

       if ($this->GetDisplayMode() == cmdbAbstractObject::ENUM_DISPLAY_MODE_VIEW) {
           // Search for ConnectableCIs linked to NetworkDevices that make the cluster
           $sOQL = "SELECT PhysicalInterface AS pi WHERE pi.aggregatelink_id = :id
              UNION SELECT LogicalInterface AS li WHERE li.aggregatelink_id = :id
              UNION SELECT NetworkDeviceVirtualInterface AS ndvi WHERE ndvi.aggregatelink_id = :id";
           $oIPInterfacesSet =  new CMDBObjectSet(DBObjectSearch::FromOQL($sOQL), array(), array('id' => $this->GetKey()));
           $sName = Dict::S('Class:AggregateLink/Tab:ipinterfaces');
           $sTitle = Dict::S('Class:AggregateLink/Tab:ipinterfaces+');
           TeemIp\TeemIp\Extension\Framework\Helper\IPUtils::DisplayTabContent($oP, $sName, 'ipinterfaces', 'IPInterface', $sTitle, '', $oIPInterfacesSet, false);
      }
   }

public static function IsAttributeRelevantForSecurityLevel(string $sAttCode, ?string $sSecLevel)
{
	switch ($sAttCode) {
		case 'community':
			return empty($sSecLevel);
		case 'security_name':
		case 'context_name':
			return !empty($sSecLevel);
		case 'auth_passphrase':
		case 'auth_protocol':
			return $sSecLevel == 'authPriv' || $sSecLevel == 'authNoPriv';
		case 'priv_passphrase':
		case 'priv_protocol':
			return $sSecLevel == 'authPriv';
		default:
			return false;
	}
}

 public function GetInitialStateAttributeFlags($sAttCode, &$aReasons = array())
	{
		$iFlags = 0;
		switch ($sAttCode) {
			case 'priv_passphrase':
				if (!empty($this->Get('priv_protocol'))) $iFlags |= OPT_ATT_MANDATORY;
				break;
			case 'auth_passphrase':
				if (!empty($this->Get('auth_protocol'))) $iFlags |= OPT_ATT_MANDATORY;
				break;
			case 'auth_protocol':
			case 'priv_protocol':
			case 'security_name':
			case 'community';
				if (static::IsAttributeRelevantForSecurityLevel($sAttCode, $this->Get('security_level'))) $iFlags |= OPT_ATT_MANDATORY;
				break;
		}

		return $iFlags | parent::GetInitialStateAttributeFlags($sAttCode, $aReasons);
    }

 public function GetAttributeFlags($sAttCode, &$aReasons = array(), $sTargetState = '')
	{
		$iFlags = 0;
		switch ($sAttCode) {
			case 'priv_passphrase':
				if (!empty($this->Get('priv_protocol'))) $iFlags |= OPT_ATT_MANDATORY;
				break;
			case 'auth_passphrase':
				if (!empty($this->Get('auth_protocol'))) $iFlags |= OPT_ATT_MANDATORY;
				break;
			case 'auth_protocol':
			case 'priv_protocol':
			case 'security_name':
			case 'community';
				if (static::IsAttributeRelevantForSecurityLevel($sAttCode, $this->Get('security_level'))) $iFlags |= OPT_ATT_MANDATORY;
				break;
		}

		return $iFlags | parent::GetAttributeFlags($sAttCode, $aReasons, $sTargetState);
    }

 public function ComputeValues()
	{
		parent::ComputeValues();

		foreach ([
			'community',
			'security_name',
			'auth_passphrase',
			'auth_protocol',
			'priv_passphrase',
			'priv_protocol',
			'context_name',
		] as $sAttCode){
			if (!static::IsAttributeRelevantForSecurityLevel($sAttCode, $this->Get('security_level'))) $this->Set($sAttCode, null);
		}
    }
